mapper.select:
select sno, stitle, sinfo, sprice from (select /*+INDEX_DESC (saleboard PK_SALEBOARD)*/ rownum rn, sno, stitle, sinfo, sprice from alt.saleBoard where
stitle like '%'||#{keyword}||'%' and
rownum <= #{pageNum} * #{amount} and sdelete = 'N' ) where rn > (#{pageNum} -1) * #{amount}

select sno, round(avg(rstar)) rstar, count(rstar) cnt from reply where rdelete = 'N' and
sno = #{item}
GROUP BY sno order by sno desc

SELECT * from alt.saleThumbImg WHERE
sno = #{item}
order by sno desc


select sum(bprice * BAMOUNT)
from alt.basket
where cid = #{cid}

select count(*) as result
from alt.zim
where cid= #{cid} and sno = #{sno}

mapper.update:

update alt.saleBoard
set sdelete = 'Y'
where sno = #{sno}
